using System;
using System.Net;
using System.Net.Http;
using System.Net.Security;
using System.Security.Cryptography.X509Certificates;
using System.Text;
using System.Threading.Tasks;
using CoreWCF.Channels;
using Helpers;
using Microsoft.AspNetCore.Hosting;
using Xunit;
using Xunit.Abstractions;
using static WSHttp.SimpleWSHTTPTest;

namespace CoreWCF.Http.Tests
{
    public class Issue1003Test
    {
        private readonly ITestOutputHelper _outputHelper;

        public Issue1003Test(ITestOutputHelper outputHelper)
        {
            _outputHelper = outputHelper;
        }

        private static async Task<string> RequestAndAssert(string url, string text, string action)
        {
            HttpClientHandler clientHandler = new HttpClientHandler();
            clientHandler.ServerCertificateCustomValidationCallback = (sender, certificate, chain, sslPolicyErrors) => { return true; };

            using (HttpClient client = new HttpClient(clientHandler))
            using (HttpContent content = new StringContent(text, Encoding.UTF8, "application/soap+xml"))
            using (HttpRequestMessage request = new HttpRequestMessage(HttpMethod.Post, url))
            {
                request.Headers.Add("SOAPAction", action);
                request.Headers.TryAddWithoutValidation("Content-Type", "application/soap+xml; charset=utf-8");
                request.Content = content;
                using (HttpResponseMessage response = await client.SendAsync(request))
                {
                    response.EnsureSuccessStatusCode(); // throws an Exception if 404, 500, etc.
                    return await response.Content.ReadAsStringAsync();
                }
            }
        }

        [Fact]
        public async Task WSHttpRequestReplyWithTransportMessageCertificateWhenSignatureElementWithPrefix()
        {
            ServicePointManager.ServerCertificateValidationCallback += (sender, certificate, chain, sslPolicyErrors) => true;
            IWebHost host = ServiceHelper.CreateHttpsWebHostBuilder<WSHttpTransportWithMessageCredentialWithCertificateNoSecurityContext>(_outputHelper).Build();
            using (host)
            {
                host.Start();

                var content = @"
<soap:Envelope xmlns:soap=""http://www.w3.org/2003/05/soap-envelope"">
  <soap:Header xmlns:wsa=""http://www.w3.org/2005/08/addressing"">
    <wsse:Security xmlns:wsse=""http://docs.oasis-open.org/wss/2004/01/oasis-200401-wss-wssecurity-secext-1.0.xsd"" xmlns:wsu=""http://docs.oasis-open.org/wss/2004/01/oasis-200401-wss-wssecurity-utility-1.0.xsd""><wsu:Timestamp wsu:Id=""TS-F221523BABC8C1C6DC169178258963930""><wsu:Created>2023-08-11T19:36:29.638Z</wsu:Created><wsu:Expires>2023-08-11T19:41:29.638Z</wsu:Expires></wsu:Timestamp><wsse:BinarySecurityToken EncodingType=""http://docs.oasis-open.org/wss/2004/01/oasis-200401-wss-soap-message-security-1.0#Base64Binary"" ValueType=""http://docs.oasis-open.org/wss/2004/01/oasis-200401-wss-x509-token-profile-1.0#X509v3"" wsu:Id=""X509-F221523BABC8C1C6DC169178258962925""></wsse:BinarySecurityToken><ds:Signature Id=""SIG-F221523BABC8C1C6DC169178258963229"" xmlns:ds=""http://www.w3.org/2000/09/xmldsig#""><ds:SignedInfo><ds:CanonicalizationMethod Algorithm=""http://www.w3.org/2001/10/xml-exc-c14n#""><ec:InclusiveNamespaces PrefixList=""wsa soap"" xmlns:ec=""http://www.w3.org/2001/10/xml-exc-c14n#""/></ds:CanonicalizationMethod><ds:SignatureMethod Algorithm=""http://www.w3.org/2000/09/xmldsig#rsa-sha1""/><ds:Reference URI=""#id-F221523BABC8C1C6DC169178258962928""><ds:Transforms><ds:Transform Algorithm=""http://www.w3.org/2001/10/xml-exc-c14n#""><ec:InclusiveNamespaces PrefixList="""" xmlns:ec=""http://www.w3.org/2001/10/xml-exc-c14n#""/></ds:Transform></ds:Transforms><ds:DigestMethod Algorithm=""http://www.w3.org/2000/09/xmldsig#sha1""/><ds:DigestValue>VCU5evmwiC+opP1vt9wqHnNAyFY=</ds:DigestValue></ds:Reference></ds:SignedInfo><ds:SignatureValue></ds:SignatureValue><ds:KeyInfo Id=""KI-F221523BABC8C1C6DC169178258962926""><wsse:SecurityTokenReference wsu:Id=""STR-F221523BABC8C1C6DC169178258962927""><wsse:Reference URI=""#X509-F221523BABC8C1C6DC169178258962925"" ValueType=""http://docs.oasis-open.org/wss/2004/01/oasis-200401-wss-x509-token-profile-1.0#X509v3""/></wsse:SecurityTokenReference></ds:KeyInfo></ds:Signature></wsse:Security>
    <wsa:Action soap:mustUnderstand=""1"">http://tempuri.org/IEchoService/EchoString</wsa:Action>
    <wsa:ReplyTo soap:mustUnderstand=""1""><wsa:Address>http://www.w3.org/2005/08/addressing/anonymous</wsa:Address></wsa:ReplyTo>
    <wsa:To soap:mustUnderstand=""1"" wsu:Id=""id-F221523BABC8C1C6DC169178258962928"" xmlns:wsu=""http://docs.oasis-open.org/wss/2004/01/oasis-200401-wss-wssecurity-utility-1.0.xsd"">http://tempuri.org/IEchoService/EchoString</wsa:To>
   </soap:Header>
   <soap:Body>
    <EchoString xmlns=""http://tempuri.org/"">
      <echo>aaaa</echo>
    </EchoString>
   </soap:Body>
</soap:Envelope>";

                string requestUri = $"https://localhost:{host.GetHttpsPort()}/WSHttpWcfService/basichttp.svc";
                string action = "http://tempuri.org/IEchoService/EchoString";
                await RequestAndAssert(requestUri, content, action);
            }
        }

        internal class WSHttpTransportWithMessageCredentialWithCertificateNoSecurityContext : WSHttpTransportWithMessageCredentialWithCertificate
        {
            public override Binding ChangeBinding(WSHttpBinding wsBInding)
            {
                wsBInding.Security.Message.EstablishSecurityContext = false;
                CustomBinding myCustomBinding = new CustomBinding(wsBInding);
                TransportSecurityBindingElement security = myCustomBinding.Elements.Find<TransportSecurityBindingElement>();
                security.LocalServiceSettings.MaxClockSkew = TimeSpan.MaxValue;

                return myCustomBinding;
            }
        }
    }
}
